@import "tailwindcss/base";
@import "tailwindcss/components";
@import "tailwindcss/utilities";

@layer components {
  .btn {
    @apply px-4 py-2.5 rounded-lg font-semibold transition-all cursor-pointer;
  }

  .btn-primary {
    @apply btn bg-blue-600 hover:bg-blue-700 dark:bg-blue-700 dark:hover:bg-blue-600 text-white;
  }

  .btn-secondary {
    @apply btn bg-slate-500 hover:bg-slate-600 dark:bg-slate-700 dark:hover:bg-slate-600 text-white;
  }

  .btn-icon {
    @apply btn bg-gray-200 hover:bg-gray-300 dark:bg-gray-700 dark:hover:bg-gray-600 text-gray-700 dark:text-gray-300 p-2;
  }

  .card {
    @apply bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded-lg shadow-lg p-6;
  }

  .card-summary {
    @apply rounded-lg border p-3 sm:p-4 shadow-sm;
  }

  .card-blue {
    @apply card-summary bg-blue-50 dark:bg-blue-900 border-blue-200 dark:border-blue-700;
  }

  .card-emerald {
    @apply card-summary bg-emerald-50 dark:bg-emerald-900 border-emerald-200 dark:border-emerald-700;
  }

  .card-violet {
    @apply card-summary bg-violet-50 dark:bg-violet-900 border-violet-200 dark:border-violet-700;
  }

  .card-orange {
    @apply card-summary bg-orange-50 dark:bg-orange-900 border-orange-200 dark:border-orange-700;
  }

  .text-summary-label {
    @apply text-xs font-semibold uppercase tracking-wide mb-1 text-gray-600 dark:text-gray-300;
  }

  .text-summary-value {
    @apply text-xl sm:text-2xl font-bold;
  }

  .text-summary-value-blue {
    @apply text-summary-value text-blue-700 dark:text-blue-100;
  }

  .text-summary-value-emerald {
    @apply text-summary-value text-emerald-700 dark:text-emerald-100;
  }

  .text-summary-value-violet {
    @apply text-summary-value text-violet-700 dark:text-violet-100;
  }

  .text-summary-value-orange {
    @apply text-summary-value text-orange-700 dark:text-orange-100;
  }
}
